package packj2;

import java.time.LocalDate;
import java.time.Period;

public abstract class Pessoa {
    private Pessoa Pai;
    private Pessoa Mae;
    private String Nome;
    private String Apelido;
    private String Morada;
    private LocalDate DataNasc;
    private int Idade;
    private char Sexo;
    ///////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
    public Pessoa(String n, String a, String m, String nasc) {
        // TODO Auto-generated constructor stub
        Nome = n ;
        Apelido = a ;
        Morada = m ;
        DataNasc = LocalDate.parse(nasc);
        //DataNasc = LocalDate.parse( (CharSequence)(nasc) , new DateTimeFormatter( DateTimeFormatter.ISO_LOCAL_DATE) );
        Idade = Period.between(DataNasc, LocalDate.now()).getYears();
    }
    ///////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
    public Pessoa() {
        // TODO Auto-generated constructor stub
        Pai = null;
        Mae = null;
    }
    ///////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
    public Pessoa(Pessoa p, Pessoa m) {
        // TODO Auto-generated constructor stub
        Pai = p;
        Mae = m;
    }
    ///////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
    public Pessoa(String n) {
        // TODO Auto-generated constructor stub
        Nome = n;
    }
    ///////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
    public Pessoa(String n, char s) {
        // TODO Auto-generated constructor stub
        Nome = n;
        Sexo = s;
    }    
    ///////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
    public Pessoa(String n, String a) {
        Nome = n;
        Apelido = a;
    }
    ///////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
    public Pessoa getPai() {
        return Pai;
    }
    ///////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
    public void setPai(Pessoa pai) {
        Pai = pai;
    }
    ///////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
    public Pessoa getMae() {
        return Mae;
    }
    ///////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
    public void setMae(Pessoa mae) {
        Mae = mae;
    }
    
    public String getNome() {
        return Nome;
    }
    
    public void setNome(String nome) {
        Nome = nome;
    }

    public String getApelido() {
        return Apelido;
    }

    public void setApelido(String apelido) {
        Apelido = apelido;
    }

    public String getMorada() {
        return Morada;
    }

    public void setMorada(String morada) {
        Morada = morada;
    }

    public LocalDate getDataNasc() {
        return DataNasc;
    }

    public void setDataNasc(LocalDate dataNasc) {
        DataNasc = dataNasc;
    }

    public int getIdade() {
        return Idade;
    }

    public void setIdade(int idade) {
        Idade = idade;
    }

    public char getSexo() {
        return Sexo;
    }

    public void setSexo(char sexo) {
        Sexo = sexo;
    }
    ///////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
}